<?php

// This file is generated by Composer
require_once __DIR__ . '/vendor/autoload.php';

use Http\Adapter\Guzzle6\Client as GuzzleClient;
use Github\Client;
use Symfony\Component\Dotenv\Dotenv;

/**
 * Search for code with GitHub API
 */
class CodeSearch
{
    /**
     * GitHub API client
     *
     * @var Github\Client
     */
    private $client;

    /**
     * GitHub API query string
     *
     * @var string
     */
    private $query;

    /**
     * Filename where output is to be saved
     *
     * @var string
     */
    private $filename;

    /**
     * Constructor
     *
     * @param string $query
     * @param string $filename
     */
    public function __construct($query, $filename)
    {
        $dotenv = new Dotenv();
        $dotenv->load(__DIR__.'/.env');
        $auth_token = $_ENV['AUTH_TOKEN'];
    
        $this->query = $query;
        $this->filename = $filename;

        $builder = new Github\HttpClient\Builder(new GuzzleClient());
        $this->client = new Github\Client($builder, 'application/vnd.github.v3.text-match+json');

        $this->client->authenticate($auth_token, null, Github\Client::AUTH_ACCESS_TOKEN);
    }

    /**
     * Perform a code search
     *
     * @return void
     */
    public function search()
    {
        try {
            $files = $this->client->api('search')->code($this->query);

            $output = array_map(
                function ($file) {
                    $data = [
                        'repository' => $file['repository']['name'],
                        'path' => $file['path'],
                        'url' => $file['html_url'],
                        'text_matches' => $file['text_matches'][0]['fragment']
                    ];
                    return $data;
                }, 
                $files['items']
            );

            print("Files found: " . count($files));
            print("RESULTS: " . PHP_EOL);

            if ($this->filename) {
                file_put_contents("./$this->filename", print_r($output, true));
            }

        } catch (\Exception $e) {
            print $e->getMessage() . PHP_EOL;
        }
    }
}

